mapper.select:
SELECT * FROM (SELECT ANS.ans_no, ANS.ques_no, ANS.answer, ANS.reg_date, ANS.comment_cnt, (SELECT NVL(SUM(CASE WHEN REC.like_type = 'UP' THEN 1 ELSE 0 END), 0) likes FROM tb_answer_like_rec REC WHERE REC.ans_no = ANS.ans_no) likes, (SELECT NVL(SUM(CASE WHEN REC.like_type = 'DOWN' THEN 1 ELSE 0 END), 0) likes FROM tb_answer_like_rec REC WHERE REC.ans_no = ANS.ans_no) dislikes, U.email, U.name, U.photo, U.occupation FROM TB_ANSWER ANS, TB_USER U WHERE ANS.ques_no = #{quesNo} AND ANS.user_email = U.email
AND ANS.ans_no < #{ansNo}
ORDER BY likes DESC, ans_no ASC) WHERE ROWNUM BETWEEN 0 AND 10

mapper.update:
UPDATE tb_answer_like_rec
SET like_type = #{likeType}
WHERE ans_no = #{ansNo} AND user_email = #{userEmail}

mapper.select:
SELECT NVL(SUM(CASE WHEN like_type = 'UP' THEN 1 ELSE 0 END), 0) likes,
       NVL(SUM(CASE WHEN like_type = 'DOWN' THEN 1 ELSE 0 END), 0) dislikes
FROM tb_answer_like_rec WHERE ans_no = #{ansNo}
