mapper.update:
UPDATE TB_QUESTION SET title = #{title},
                 content = #{content}
WHERE ques_no=#{quesNo}

mapper.select:
SELECT *
FROM
    (SELECT QUES.ques_no,
            QUES.question,
            QUES.category_name
    FROM tb_question QUES, tb_user U


            QUES.ques_no < #{quesNo}


            AND QUES.answered = #{answered}


            AND QUES.user_no = #{userNo}
            AND U.email = QUES.user_email


            AND QUES.category_name = #{categoryName}


    ORDER BY QUES.ques_no DESC, QUES.view_cnt DESC)
WHERE ROWNUM BETWEEN 0 AND 10

SELECT * FROM (SELECT ROW_NUMBER() OVER(PARTITION BY ques_no ORDER BY LIKES DESC, ans_no ASC) AS RN, PAIR.* FROM (SELECT QUES.ques_no, QUES.question, QUES.category_name, ANS.ans_no, ANS.answer, ANS.reg_date, ANS.comment_cnt, NVL(SUM(CASE WHEN REC.like_type = 'UP' THEN 1 ELSE 0 END), 0) likes, NVL(SUM(CASE WHEN REC.like_type = 'DOWN' THEN 1 ELSE 0 END), 0) dislikes, U.email, U.name, U.photo, U.occupation FROM TB_QUESTION QUES, TB_USER U, TB_ANSWER ANS LEFT OUTER JOIN TB_LIKE_REC REC ON REC.ans_no = ANS.ans_no WHERE ANS.ques_no = QUES.ques_no AND U.email = ANS.user_email AND QUES.answered = 'Y'
AND QUES.ques_no < #{quesNo}
AND QUES.category_name = #{categoryName}
GROUP BY QUES.ques_no, QUES.question, QUES.category_name, ANS.ans_no, ANS.answer, ANS.reg_date, ANS.comment_cnt, U.email, U.name, U.photo, U.occupation) PAIR) WHERE RN=1 AND ROWNUM BETWEEN 0 AND 10
