mapper.update:
UPDATE TB_QUESTION SET title = #{title},
                 content = #{content}
WHERE ques_no=#{quesNo}

mapper.select:
SELECT *
FROM tb_question QUES,
     tb_category CAT
WHERE QUES.ques_no > #{quesNo}

  AND QUES.answered = #{answered}


  AND QUES.category_code = #{categoryCode}

  AND CAT.category_code = QUES.category_code
  AND ROWNUM BETWEEN 0 AND 10

SELECT * FROM tb_question QUES, tb_category CAT, (SELECT rownum AS RN, answer, reg_date, FROM tb_answer ORDER BY like_cnt DESC, ans_no ASC) ANS, tb_user U WHERE QUES.ques_no > #{quesNo}
AND QUES.category_code = #{categoryCode}
AND CAT.category_code = QUES.category_code AND ROWNUM BETWEEN 0 AND 10 AND ANS.ques_no = QUES.ques_no AND ANS.RN = 1
