mapper.update:
UPDATE TB_QUESTION SET title = #{title},
                 content = #{content}
WHERE ques_no=#{quesNo}

mapper.select:
SELECT * FROM (SELECT QUES.ques_no, QUES.question, (SELECT category_name FROM tb_category WHERE category_code = QUES.category_code) category_name, QUES.category_code, QUES.reg_date FROM tb_question QUES, tb_user U WHERE QUES.user_email = U.email AND QUES.answered = 'N'
AND QUES.ques_no < #{quesNo}
AND QUES.answered = #{answered}
AND QUES.userEmail = #{userEmail} AND U.email = QUES.user_email
AND QUES.category_code = #{categoryCode}
ORDER BY QUES.ques_no DESC, QUES.view_cnt DESC) WHERE ROWNUM BETWEEN 0 AND 10

SELECT * FROM (SELECT ROW_NUMBER() OVER(PARTITION BY ques_no ORDER BY LIKES DESC, ans_no ASC) AS RN, PAIR.* FROM (SELECT QUES.ques_no, QUES.question, (SELECT category_name FROM tb_category WHERE category_code = QUES.category_code) category_name, QUES.category_code, ANS.ans_no, ANS.answer, ANS.reg_date, ANS.comment_cnt, (SELECT NVL(SUM(CASE WHEN REC.like_type = 'UP' THEN 1 ELSE 0 END), 0) likes FROM tb_answer_like_rec REC WHERE REC.ans_no = ANS.ans_no) likes, (SELECT NVL(SUM(CASE WHEN REC.like_type = 'UP' THEN 1 ELSE 0 END), 0) dislikes FROM tb_answer_like_rec REC WHERE REC.ans_no = ANS.ans_no) dislikes, U.email, U.name, U.photo, U.occupation FROM tb_question QUES, tb_answer ANS, tb_user U WHERE ANS.ques_no = QUES.ques_no AND U.email = ANS.user_email AND QUES.answered = 'Y'
AND QUES.ques_no < #{quesNo}
AND QUES.category_code = #{categoryCode} ) PAIR) WHERE RN = 1 AND ROWNUM BETWEEN 0 AND 10
