mapper.update:
UPDATE TB_QUESTION SET title = #{title},
                 content = #{content}
WHERE ques_no=#{quesNo}

mapper.select:
SELECT *
FROM
    (SELECT QUES.ques_no,
            QUES.question,
            QUES.category_name
    FROM tb_question QUES, tb_user U


            QUES.ques_no < #{quesNo}


            AND QUES.answered = #{answered}


            AND QUES.user_no = #{userNo}
            AND U.user_no = QUES.user_no


            AND QUES.category_name = #{categoryName}


    ORDER BY QUES.ques_no DESC, QUES.view_cnt DESC)
WHERE ROWNUM BETWEEN 0 AND 10

SELECT * FROM (SELECT ROW_NUMBER() OVER(PARTITION BY ANS.ques_no ORDER BY like_cnt DESC, ans_no ASC) ANS_RN, QUES.ques_no, QUES.question, QUES.category_name, ANS.ans_no, ANS.answer, ANS.reg_date, ANS.like_cnt, U.user_no, U.email, U.name, U.photo, U.occupation FROM tb_answer ANS, tb_question QUES, tb_user U WHERE ANS.ques_no = QUES.ques_no
AND QUES.ques_no < #{quesNo}
AND U.user_no = ANS.user_no AND QUES.answered = 'Y'
AND QUES.category_name = #{categoryName}
ORDER BY QUES.ques_no DESC, QUES.view_cnt DESC) WHERE ANS_RN = 1 AND ROWNUM BETWEEN 0 AND 10
